def f1(driver,num):

    locator=(By.CLASS_NAME,"morelist")
    WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))

    url=driver.current_url 
    cnum=int(re.findall("Paging=([0-9]{1,})",url)[0])
    if num!=cnum:
        url=re.sub("(?<=Paging=)[0-9]{1,}",str(num),url)
        driver.get(url)
        locator=(By.CLASS_NAME,"morelist")
        WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))

    page=driver.page_source

    soup=BeautifulSoup(page,"lxml")

    div=soup.find("div",class_="morelist")

    table=div.find("table")

    trs=table.find_all("tr","trfont")
    data=[]
    for tr in trs:
        a=tr.find("a")
        span=tr.find("span")
        tmp=[a["title"],"http://ggzy.enshi.gov.cn"+a["href"],span.text.strip()]
        data.append(tmp)
    df=pd.DataFrame(data=data)
    df["info"]=None
    return df 

def f2(driver):

    locator=(By.CLASS_NAME,"morelist")
    WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))
    locator=(By.XPATH,"//td[@class='huifont']")
    WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))
    total=driver.find_element_by_xpath("//td[@class='huifont']").text.split("/")[1]
    total=int(total)
    driver.quit()
    return total



def f3(driver,url):


    driver.get(url)

    locator=(By.XPATH,"//div[@class='row mt10']")

    WebDriverWait(driver,10).until(EC.presence_of_all_elements_located(locator))

    before=len(driver.page_source)
    time.sleep(0.1)
    after=len(driver.page_source)
    i=0
    while before!=after:
        before=len(driver.page_source)
        time.sleep(0.1)
        after=len(driver.page_source)
        i+=1
        if i>5:break

    page=driver.page_source

    soup=BeautifulSoup(page,'lxml')

    div=soup.find('div',class_='row mt10')
    #div=div.find_all('div',class_='ewb-article')[0]
    
    return div

