def f3(driver, url):
    driver.get(url)
    locator = (By.CLASS_NAME, "detail-info")
    WebDriverWait(driver, 30).until(EC.presence_of_element_located(locator))
    sleep(0.5)
    before = len(driver.page_source)
    sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break

    page = driver.page_source
    soup = BeautifulSoup(page, 'html.parser')
    div = soup.find('div', class_='detail-info')
    return div


def f1(driver, num):

    locator = (By.XPATH, '//*[@id="showList"]/li[1]/a')
    WebDriverWait(driver, 30).until(EC.visibility_of_element_located(locator))
    val = driver.find_element_by_xpath('//*[@id="showList"]/li[1]/a').get_attribute("href")[-50:]
    sleep(1)
    cnum = int(driver.find_element_by_class_name('pg_maxpagenum').text.split('/')[0])
    if cnum != int(num):
        locator = (By.CLASS_NAME, 'pg_num_input')
        WebDriverWait(driver, 30).until(EC.visibility_of_element_located(locator))
        driver.find_element_by_class_name("pg_num_input").clear()
        driver.find_element_by_class_name("pg_num_input").send_keys(num)
        WebDriverWait(driver, 30).until(EC.visibility_of_element_located((By.CLASS_NAME, "pg_gobtn")))
        driver.find_element_by_class_name("pg_gobtn").click()
        locator = (By.XPATH, '//*[@id="showList"]/li[1]/a[not(contains(@href,"%s"))]' % val)
        WebDriverWait(driver, 30).until(EC.visibility_of_element_located(locator))
    data = []
    page = driver.page_source
    body = etree.HTML(page)
    content_list = body.xpath("//div[@id='showList']/li")
    p1 = body.xpath("//li[@class='wb-tree-items haschild current']/h3/a/@href")[0].split('\'')[1]
    p2 = body.xpath("//a[@class='current']/@href")[0].split('\'')[1]
    p = '/'+p1 + "/" +p2 + "/"
    for content in content_list:
        name = content.xpath("./a/text()")[0].strip()
        ggstart_time = content.xpath("./span/text()")[0].strip()
        url = "http://58.216.50.99:8089/jyzx" +p + ''.join(ggstart_time.split('-')) +'/'+content.xpath("./a/@href")[0].split('\"')[1] + '.html'
        temp = [name, ggstart_time, url]
        data.append(temp)
    df = pd.DataFrame(data=data)
    df['info'] = None
    return df


def f2(driver):
    while True:
        locator = (By.CLASS_NAME, 'pg_maxpagenum')
        WebDriverWait(driver, 30).until(EC.visibility_of_element_located(locator))
        total_page = int(driver.find_element_by_class_name('pg_maxpagenum').text.split('/')[1])
        sleep(0.5)
        locator = (By.CLASS_NAME, 'pg_maxpagenum')
        WebDriverWait(driver, 30).until(EC.visibility_of_element_located(locator))

        total_page2 = int(driver.find_element_by_class_name('pg_maxpagenum').text.split('/')[1])
        if total_page == total_page2:
            break
    driver.quit()
    return total_page


def switch(driver, xmtype, ggtype):
    locator = (By.CLASS_NAME, 'span231')
    WebDriverWait(driver, 30).until(EC.visibility_of_all_elements_located(locator))
    locator = (By.XPATH, "//li[@class='wb-tree-items haschild']/h3")
    WebDriverWait(driver, 30).until(EC.visibility_of_all_elements_located(locator))
    try:
        driver.find_element_by_xpath("//li[@class='wb-tree-items haschild']/h3[contains(string(),'%s')]"%xmtype).click()
    except:
        driver.find_element_by_xpath(
            "//li[@class='wb-tree-items haschild current']/h3[contains(string(),'%s')]" % xmtype).click()
        driver.find_element_by_xpath(
            "//li[@class='wb-tree-items haschild']/h3[contains(string(),'%s')]" % xmtype).click()
    driver.find_element_by_xpath(
        "//li[@class='wb-tree-items haschild current']/ul/li[contains(string(),'%s')]"%ggtype).click()

def before(f, xmtype, ggtype):
    def wrap(*args):
        driver = args[0]
        switch(driver, xmtype, ggtype)
        return f(*args)
    return wrap

