def f1(driver, num):
    locator = (By.CLASS_NAME, "categorypagingcontent")
    WebDriverWait(driver, 20).until(EC.presence_of_all_elements_located(locator))
    val = driver.find_element_by_xpath("//li[@class='menu-right-item clearfix'][1]/a").get_attribute("href")[-60:]
    try:
        cnum = int(
            driver.find_element_by_xpath('//*[@id="categorypagingcontent"]/div/div[2]/div/ul/li[13]/a').text.split('/')[
                0])
    except:
        cnum = 1

    if int(num) != cnum:
        driver.find_element_by_id("GoToPagingNo").clear()
        driver.find_element_by_id("GoToPagingNo").send_keys(num)
        driver.find_element_by_xpath('//*[@id="categorypagingcontent"]/div/div[2]/div/ul/li[16]/a').click()
        locator = (By.XPATH, "//li[@class='menu-right-item clearfix'][1]/a[not(contains(@href,'%s'))]" % val)
        WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))

    page = driver.page_source
    body = etree.HTML(page)
    data = []
    content_list = body.xpath("//ul[@class='menu-right-items']/li")
    for content in content_list:
        name = content.xpath('./a/text()')[0]
        ggstart_time = content.xpath('./span/text()')[0].strip()
        url = "http://www.haztb.gov.cn" + content.xpath("./a/@href")[0]
        temp = [name, ggstart_time, url]
        data.append(temp)
    df = pd.DataFrame(data=data)
    df["info"] = None
    return df


def f2(driver):
    locator = (By.CLASS_NAME, "categorypagingcontent")
    WebDriverWait(driver, 20).until(EC.presence_of_all_elements_located(locator))
    try:
        total_page = \
            driver.find_element_by_xpath('//*[@id="categorypagingcontent"]/div/div[2]/div/ul/li[13]/a').text.split('/')[
                1]
    except:
        total_page = 1
    driver.quit()
    return int(total_page)


def f3(driver, url):
    driver.get(url)
    locator = (By.CLASS_NAME, "article-block")
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break

    page = driver.page_source
    soup = BeautifulSoup(page, 'html.parser')
    div = soup.find('div', class_='article-block')
    # print(div)
    return div


