_name_='jiangyin'
def f3(driver, url):
    driver.get(url)
    locator = (By.CLASS_NAME, "ind35")
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))

    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break

    page = driver.page_source
    soup = BeautifulSoup(page, 'html.parser')
    div = soup.find('div', class_='ind35')
    return div


def f1(driver, num):
    # zfcg_yucai_gg和gcjs_liubiao_gg 两个表都是shtml结尾。liubiao需要翻页，yucai内容不够不要翻页，后期内容多了，或许需要修改js翻页代码。
    if "shtml" in driver.current_url:

        locator = (By.XPATH, '//*[@id="列表"]/ul/li')
        WebDriverWait(driver, 30).until(EC.visibility_of_all_elements_located(locator))
        val = driver.find_element_by_xpath('//*[@id="列表"]/ul/li[1]/a').get_attribute("href")[-20:]
        locator = (By.XPATH, '//*[@id="page_列表"]/font/select/option')
        WebDriverWait(driver, 30).until(EC.visibility_of_all_elements_located(locator))
        cnum = driver.find_element_by_xpath('//*[@id="page_列表"]/font/select/option').text
        if int(num) != int(cnum):
            # 预采和流标js_id可能不同
            driver.execute_script("requestPage('/websites/page/page.jsp?columnId=7zk53rk5y5qa&name=%E5%88%97%E8%A1%A8&tempId=1fhv6ub5kskf0&pageno={}&lang=zh');".format(int(num)-1))
            locator = (By.XPATH,'//*[@id="列表"]/ul/li[1]/a[not(contains(@href,"%s"))]'%val)
            WebDriverWait(driver, 30).until(EC.visibility_of_element_located(locator))
        page = driver.page_source
        body = etree.HTML(page)
        content_list = body.xpath('//*[@id="列表"]/ul/li')
        data = []
        for content in content_list:
            name = content.xpath("./a/text()")[0]
            ggstart_time = content.xpath("./span/text()")[0]
            url = "http://www.jiangyin.gov.cn" + content.xpath("./a/@href")[0]
            temp = [name, ggstart_time, url]
            data.append(temp)
            print(temp)
    else:
        page = driver.page_source
        cnum = re.findall("selected&gt;(\d+)&lt;\/option", page)[0]
        if int(cnum) != int(num):
            before = len(driver.page_source)
            time.sleep(0.1)
            after = len(driver.page_source)
            i = 0
            while before != after:
                before = len(driver.page_source)
                time.sleep(0.1)
                after = len(driver.page_source)
                i += 1
                if i > 5: break

            url = "=".join('&'.join(driver.current_url.split("&")[:-1]).split('=')[:-1]) + "=" + str(
                num - 1) + "&lang=zh"
            driver.get(url)
        data = []
        content_list = re.findall("&lt;li&gt;(.*?)&lt;/li&gt;", driver.page_source)
        for content in content_list:
            name = re.findall("&gt;(.*?)&lt;/a&gt;", content)[0]
            ggstart_time = re.findall('data-date=\\\\\"(.*?)\\\\\"', content)[0]
            url = "http://www.jiangyin.gov.cn" + re.findall('&lt;a href=\\\\\"(.*?)\\\\\"', content)[0]
            temp = [name, ggstart_time, url]
            data.append(temp)
    df = pd.DataFrame(data=data)
    df['info'] = None
    return df


def f2(driver):
    page = driver.page_source
    total_page = re.findall("共(\d+)页", page)[0]
    driver.quit()
    return int(total_page)


