_name_="nantong"
def f3(driver, url):
    driver.get(url)
    locator = (By.CLASS_NAME, "article-info")
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
    before = len(driver.page_source)
    sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break

    page = driver.page_source
    soup = BeautifulSoup(page, 'html.parser')
    div = soup.find('div', class_='article-info')
    return div


def f1(driver, num):
    locator = (By.XPATH, '//*[@id="xxList"]/tr[1]/td[4]/a')
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
    val = driver.find_element_by_xpath('//*[@id="xxList"]/tr[1]/td[4]/a').get_attribute("href")[-50:]
    sleep(1)
    cnum = int(driver.find_element_by_class_name('pg_maxpagenum').text.split('/')[0])
    if cnum != int(num):
        locator = (By.CLASS_NAME, 'pg_num_input')
        WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
        driver.find_element_by_class_name("pg_num_input").clear()
        driver.find_element_by_class_name("pg_num_input").send_keys(num)
        WebDriverWait(driver, 20).until(EC.visibility_of_element_located((By.CLASS_NAME, "pg_gobtn")))
        driver.find_element_by_class_name("pg_gobtn").click()
        locator = (By.XPATH, '//*[@id="xxList"]/tr[1]/td[4]/a[not(contains(@href,"%s"))]' % val)
        WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
    data = []
    page = driver.page_source
    body = etree.HTML(page)
    content_list = body.xpath("//tbody[@id='xxList']//tr")
    for content in content_list:
        name = ''.join(content.xpath("./td/a/text()")).strip()
        ggstart_time = content.xpath("./td[6]/text()")[0].strip()
        url = "http://ggzyjy.ntzw.gov.cn" + content.xpath("./td/a/@href")[0]
        temp = [name, ggstart_time, url]
        data.append(temp)
    df = pd.DataFrame(data=data)
    df['info'] = None
    driver.refresh()
    return df


def f2(driver):
    while True:
        locator = (By.CLASS_NAME, 'pg_maxpagenum')
        WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
        total_page = int(driver.find_element_by_class_name('pg_maxpagenum').text.split('/')[1])
        sleep(1)
        total_page2 = int(driver.find_element_by_class_name('pg_maxpagenum').text.split('/')[1])
        if total_page == total_page2:
            break
    driver.quit()
    return total_page


def switch(driver, xmtype, ggtype):
    locator = (By.XPATH, '//*[@class="pagination showlistdisplay"]/span')
    WebDriverWait(driver, 30).until(EC.visibility_of_element_located(locator))
    val = driver.find_element_by_xpath('//*[@class="pagination showlistdisplay"]/span[@class="pg_gongPageNum"]').text
    driver.execute_script(type[xmtype][ggtype])
    locator = (By.XPATH,'//*[@class="pagination showlistdisplay"]/span[@class="pg_gongPageNum"][not(contains(string(),"%s"))]' % val)
    WebDriverWait(driver, 30).until(EC.visibility_of_element_located(locator))


def before(f, xmtype, ggtype):
    def wrap(*args):
        driver = args[0]
        switch(driver, xmtype, ggtype)
        return f(*args)
    return wrap

