def f1(driver, num):
    locator = (By.XPATH, "//tr[@class='ewb-trade-tr'][1]/td[2]/a")
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
    locator = (By.CLASS_NAME, 'pg_maxpagenum')
    WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
    val = driver.find_element_by_xpath("//tr[@class='ewb-trade-tr'][1]/td[2]/a").get_attribute("href")[-50:]
    cnum = int(driver.find_element_by_class_name("pg_maxpagenum").text.split('/')[0])
    if cnum != int(num):
        time.sleep(1)
        driver.find_element_by_class_name('pg_num_input').clear()
        driver.find_element_by_class_name('pg_num_input').send_keys(num)
        driver.find_element_by_class_name("pg_gobtn").click()
        locator = (By.XPATH, "//tbody[@id='showList']/tr[1]//a[not(contains(@href,'%s'))]" % val)
        WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
    locator = (By.CLASS_NAME, "ewb-trade-tb")
    WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
    locator = (By.CLASS_NAME, 'pg_maxpagenum')
    WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))

    data = []
    page = driver.page_source
    body = etree.HTML(page)
    content_list = body.xpath("//tbody[@id='showList']/tr[@class='ewb-trade-tr']")
    for content in content_list:
        name = content.xpath("./td[2]/a/text()")[0]
        ggstart_time = content.xpath('./td[4]/text()')[0]
        url = "http://ggzy.sqzwfw.gov.cn" + content.xpath("./td[2]/a/@href")[0]
        temp = [name, ggstart_time, url]
        data.append(temp)
    df = pd.DataFrame(data=data)
    df["info"] = None
    return df


def f2(driver):
    locator = (By.XPATH, '//*[@id="divInfoReportPage"]/span[5]')
    WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
    while True:
        locator = (By.CLASS_NAME, 'pg_maxpagenum')
        WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
        total_page = int(driver.find_element_by_class_name("pg_maxpagenum").text.split('/')[1])
        time.sleep(0.3)
        total_page2 = int(driver.find_element_by_xpath('//span[@class="pg_maxpagenum"]').text.split('/')[1])
        if total_page == total_page2:
            break
    driver.quit()
    return int(total_page)


def before(f, xmtype, ggtype):
    def wrap(*args):
        driver = args[0]
        switch(driver, xmtype, ggtype)
        return f(*args)

    return wrap


