def f3(driver, url):
    driver.get(url)
    locator = (By.ID, "tblInfo")
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))

    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break

    page = driver.page_source
    soup = BeautifulSoup(page, 'html.parser')
    div = soup.find('table', id='tblInfo')
    return div


def f1(driver, num):
    locator = (By.XPATH, '//*[@id="MoreInfoList1_DataGrid1"]/tbody/tr[1]/td[2]/a')
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
    if driver.current_url.split('=')[1][:-3] == '004003002':
        for i, p in enumerate(PAGE):
            if num <= p:
                driver.get(URLS[i])
                break
            else:
                num -= p
    val = driver.find_element_by_xpath('//*[@id="MoreInfoList1_DataGrid1"]/tbody/tr[1]/td[2]/a').get_attribute("href")[-50:]
    cnum = driver.find_element_by_xpath('//*[@id="MoreInfoList1_Pager"]//td[1]/font[3]/b').text
    if int(cnum) != int(num):
        driver.execute_script("javascript:__doPostBack('MoreInfoList1$Pager','{}')".format(num))
        locator = (
        By.XPATH, '//*[@id="MoreInfoList1_DataGrid1"]/tbody/tr[1]/td[2]/a[not(contains(@href,"%s"))]' % val)
        WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))

    locator =(By.XPATH,'//*[@id="MoreInfoList1_DataGrid1"]/tbody/tr/td')
    WebDriverWait(driver,30).until(EC.visibility_of_all_elements_located(locator))
    data = []
    page = driver.page_source
    body = etree.HTML(page)
    content_list = body.xpath("//table[@id='MoreInfoList1_DataGrid1']//tr")
    for content in content_list:
        name = content.xpath("./td/a/text()")[0].strip()
        ggstart_time = content.xpath("./td[3]/text()")[0].strip()
        url = "http://58.222.225.18" + content.xpath("./td/a/@href")[0]
        temp = [name, ggstart_time, url]
        data.append(temp)
    df = pd.DataFrame(data=data)
    df['info'] = None
    return df


def f2(driver):
    global PAGE
    PAGE = []
    locator = (By.XPATH, '//*[@id="MoreInfoList1_Pager"]/table//font[2]/b')
    WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
    if driver.current_url.split('=')[1] == '004003002001':
        total_page = 0
        for URL in URLS:
            driver.get(URL)
            page_temp = int(driver.find_element_by_xpath('//*[@id="MoreInfoList1_Pager"]/table//font[2]/b').text)
            PAGE.append(page_temp)
            total_page += page_temp
    else:
        total_page = int(driver.find_element_by_xpath('//*[@id="MoreInfoList1_Pager"]/table//font[2]/b').text)

    return total_page


