def f3(driver, url):
    driver.get(url)
    locator = (By.CLASS_NAME, "mainCont")
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))

    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break

    page = driver.page_source
    soup = BeautifulSoup(page, 'html.parser')
    div = soup.find('div', class_='mainCont')
    return div


def f1(driver, num):
    locator = (By.XPATH, '//*[@id="ny_cg01"]/div/ul/li[1]/a')
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
    val = driver.find_element_by_xpath('//*[@id="ny_cg01"]/div/ul/li[1]/a').get_attribute("href")[-30:]
    locator = (By.XPATH, "//a[@class='this']")
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
    cnum = driver.find_element_by_xpath("//a[@class='this']").text
    if int(cnum) != int(num):
        driver.find_element_by_id("pnum").clear()
        driver.find_element_by_id("pnum").send_keys(num)
        driver.find_element_by_id("pageSubmit").click()
        locator = (By.XPATH, '//*[@id="ny_cg01"]/div/ul/li[1]/a[not(contains(@href,"%s"))]' % val)
        WebDriverWait(driver, 30).until(EC.visibility_of_element_located(locator))
    locator =(By.XPATH,'//*[@id="ny_cg01"]/div/ul/li')
    WebDriverWait(driver,30).until(EC.visibility_of_all_elements_located(locator))
    data = []
    page = driver.page_source
    body = etree.HTML(page)
    content_list = body.xpath('//*[@id="ny_cg01"]/div/ul/li')
    for content in content_list:
        name = content.xpath("./a/text()")[0].strip()
        ggstart_time = content.xpath("./span/text()")[0]
        url = "http://xzfw.wuxi.gov.cn" + content.xpath("./a/@href")[0]
        temp = [name, ggstart_time, url]
        data.append(temp)
    df = pd.DataFrame(data=data)
    df['info'] = None
    return df


def f2(driver):
    locator = (By.ID, 'pages')
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
    total_page = int(driver.find_element_by_xpath('//div[@id="pages"]').text.split('\n')[0][2:-1])
    driver.quit()
    return total_page


