_name_="zhenjiang"
def f3(driver, url):
    driver.get(url)
    locator = (By.CLASS_NAME, "article-block")
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))

    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break

    page = driver.page_source
    soup = BeautifulSoup(page, 'html.parser')
    div = soup.find('div', class_='article-block')
    return div


def f1(driver, num):
    locator = (By.XPATH, '//*[@id="showList"]/li[1]/a')
    WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
    val = driver.find_element_by_xpath('//*[@id="showList"]/li[1]/a').get_attribute("href")[-40:]
    locator = (By.CLASS_NAME, 'pg_maxpagenum')
    WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
    cnum = driver.find_element_by_class_name('pg_maxpagenum').text.split('/')[0]
    if int(cnum) != int(num):
        driver.find_element_by_class_name("pg_num_input").clear()
        driver.find_element_by_class_name("pg_num_input").send_keys(num)
        driver.find_element_by_class_name("pg_gobtn").click()
        locator = (By.XPATH, '//*[@id="showList"]/li[1]/a[not(contains(@href,"%s"))]' % val)
        WebDriverWait(driver, 30).until(EC.visibility_of_element_located(locator))
    locator =(By.XPATH,'//*[@id="showList"]/li')
    WebDriverWait(driver,30).until(EC.visibility_of_all_elements_located(locator))
    data = []
    # cnum2 = driver.find_element_by_class_name('pg_maxpagenum').text.split('/')[0]
    #     # print("起始页：",cnum,"当前页：",cnum2,"目标页：",num)

    page = driver.page_source
    body = etree.HTML(page)
    content_list = body.xpath('//*[@id="showList"]/li')
    for content in content_list:
        name = content.xpath("./a/text()")[0].strip()
        ggstart_time = content.xpath("./span/text()")[0][1:-1]
        url = "http://ggzy.zhenjiang.gov.cn" + content.xpath("./a/@href")[0]
        temp = [name, ggstart_time, url]
        data.append(temp)

    df = pd.DataFrame(data=data)
    df['info'] = None
    return df


def f2(driver):
    locator = (By.CLASS_NAME, 'pg_maxpagenum')
    WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
    total_page = int(driver.find_element_by_class_name('pg_maxpagenum').text.split('/')[1])
    driver.quit()
    return total_page


