def f3(driver, url):
    driver.get(url)
    if "http://ggzy.zgcy.gov.cn" in driver.current_url:
        locator = (By.ID, "tblInfo")
        WebDriverWait(driver, 10).until(EC.presence_of_all_elements_located(locator))
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i = 0
        while before != after:
            before = len(driver.page_source)
            time.sleep(0.1)
            after = len(driver.page_source)
            i += 1
            if i > 5: break
        page = driver.page_source
        soup = BeautifulSoup(page, 'lxml')
        div = soup.find('div',id="tblInfo")

        return div

    else:
        try:
            locator = (By.ID, "tblInfo")
            WebDriverWait(driver, 10).until(EC.presence_of_all_elements_located(locator))
            flag = 1
        except:
            locator = (By.XPATH,"//td[@valign='top']/table[@cellpadding='0']")
            WebDriverWait(driver,20).until(EC.presence_of_all_elements_located(locator))
            flag = 2
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i = 0
        while before != after:
            before = len(driver.page_source)
            time.sleep(0.1)
            after = len(driver.page_source)
            i += 1
            if i > 5: break
        page = driver.page_source
        soup = BeautifulSoup(page, 'lxml')
        if flag == 1:
            div = soup.find('table', id='tblInfo')
        else:
            div = soup.find("table",cellpadding='15')
        return div

