_name_='panjin'
def f1(driver, num):
    locator = (By.XPATH,'//span[@id="currentpage"]')
    WebDriverWait(driver,20).until(EC.presence_of_element_located(locator))
    cnum = driver.find_element_by_xpath('//span[@id="currentpage"]').text
    locator = (By.XPATH, '//*[@id="contentsContainerList"]/li/a')
    WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
    val = driver.find_element_by_xpath('//*[@id="contentsContainerList"]/li[1]/a').get_attribute("href")[-20:]
    if int(cnum) != int(num):
        driver.execute_script("home.showPagenumListFunction(%s);"%num)
        locator = (
            By.XPATH, '//*[@id="contentsContainerList"]/li[1]/a[not(contains(@href,"%s"))]' % val)
        WebDriverWait(driver, 20).until(EC.presence_of_element_located(locator))
        locator = (By.XPATH, '//span[@id="currentpage"]')
        WebDriverWait(driver, 20).until(EC.presence_of_element_located(locator))
    locator = (By.ID, 'contentsContainerList')
    WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
    data = []
    page = driver.page_source
    body = etree.HTML(page)
    content_list = body.xpath('//ul[@id="contentsContainerList"]/li')
    for content in content_list:
        name = ''.join(content.xpath("./span[1]/text()")[0].split('\n')) + content.xpath("./a/text()")[0].strip()
        ggstart_time = content.xpath("./span[2]/text()")[0].strip()
        url = "http://ggb.panjin.gov.cn"+content.xpath("./a/@href")[0].strip()
        temp = [name, ggstart_time, url]
        data.append(temp)
    df = pd.DataFrame(data=data)
    df["info"] = None
    return df

def f2(driver):
    locator = (By.XPATH,'//span[@id="totalnum"]')
    WebDriverWait(driver,20).until(EC.presence_of_element_located(locator))
    total_page = driver.find_element_by_xpath('//span[@id="totalnum"]').text
    driver.quit()
    return int(total_page)

def f3(driver, url):
    driver.get(url)
    locator = (By.XPATH, "//div[@class='article']")
    WebDriverWait(driver, 10).until(EC.presence_of_all_elements_located(locator))
    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break
    page = driver.page_source
    soup = BeautifulSoup(page, 'lxml')
    div = soup.find('div',class_="article")
    # print(div)
    return div


