def f1(driver, num):
    if "zfcg" in driver.current_url:
        locator = (By.XPATH, "//tr[@height='30']/td/a")
        WebDriverWait(driver, 20).until(EC.presence_of_all_elements_located(locator))
        val = driver.find_element_by_xpath("//tr[@height='30'][1]/td/a").get_attribute("href")[-50:]
        cnum = driver.find_element_by_xpath("//td[@class='yahei redfont']").text
        if int(cnum) != int(num):
            driver.execute_script("window.location.href='./?Paging=%s'" % num)
            locator = (By.XPATH, "//tr[@height='30']/td/a[not(contains(@href,'%s'))]" % val)
            WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
        data = []
        page = driver.page_source
        body = etree.HTML(page)
        content_list = body.xpath("//tr[@height='30']")
        for content in content_list:
            name = content.xpath("./td/a")[0].xpath("string(.)").strip()
            ggstart_time = content.xpath("./td[last()]/text()")[0].strip()
            url = "http://www.ordosggzyjy.org.cn" + content.xpath("./td/a/@href")[0].strip()
            temp = [name, ggstart_time, url]
            data.append(temp)
            # print(temp)
    else:
        if driver.page_source.find("iframe") != -1 :
            frame = driver.find_element_by_xpath("//iframe[@width='740']")
            driver.switch_to.frame(frame)
        locator = (By.XPATH, '//*[@id="DataGrid1"]/tbody/tr/td/a')
        WebDriverWait(driver, 20).until(EC.presence_of_all_elements_located(locator))
        val = driver.find_element_by_xpath('//*[@id="DataGrid1"]/tbody/tr[1]/td/a').get_attribute("href")[-50:]
        cnum = driver.find_element_by_xpath("//font[@color='red']/b").text
        if int(cnum) != int(num):
            driver.execute_script("javascript:__doPostBack('Pager','%s')" % num)
            locator = (By.XPATH, "//*[@id='DataGrid1']/tbody/tr[1]/td/a[not(contains(@href,'%s'))]" % val)
            WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
        data = []
        page = driver.page_source
        body = etree.HTML(page)
        content_list = body.xpath("//table[@id='DataGrid1']//tr")
        for content in content_list:
            name = content.xpath("./td/a/text()")[0].strip()
            ggstart_time = content.xpath("./td[last()]/text()")[0].strip()
            url = "http://www.ordosggzyjy.org.cn" + content.xpath("./td/a/@href")[0].strip()
            temp = [name, ggstart_time, url]
            data.append(temp)
    df = pd.DataFrame(data=data)
    df["info"] = None
    return df


def f2(driver):
    if "zfcg" in driver.current_url:
        total_page = re.findall("\/(\d+)",driver.find_element_by_xpath('//td[@class="huifont"]').text)[0]
    else:
        if driver.page_source.find("iframe") != -1:
            frame = driver.find_element_by_xpath("//iframe[@width='740']")
            driver.switch_to.frame(frame)
        locator = (By.ID, "Pager")
        WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
        total_page = driver.find_element_by_xpath('//*[@id="Pager"]/div/font[2]/b').text

    driver.quit()
    return int(total_page)


def f3(driver, url):
    driver.get(url)

    locator = (By.XPATH, "//table[@id='tblInfo']")
    WebDriverWait(driver, 30).until(EC.presence_of_all_elements_located(locator))
    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break
    page = driver.page_source
    soup = BeautifulSoup(page, 'lxml')
    div = soup.find('table', id='tblInfo')
    return div


