def f1(driver, num):
    locator = (By.XPATH,"//table[@id='MoreInfoList1_DataGrid1']//td/a")
    WebDriverWait(driver, 20).until(EC.presence_of_all_elements_located(locator))
    val = driver.find_element_by_xpath("//table[@id='MoreInfoList1_DataGrid1']//tr[1]/td/a").get_attribute("href")[-50:]
    try:
        locator = (By.XPATH, "//td[@class='yahei redfont']")
        WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
        cnum = driver.find_element_by_xpath("//font[@style='font-weight:Bold;color:red;']").text
    except:
        cnum = 1
    if int(cnum) != int(num):
        driver.execute_script("javascript:__doPostBack('MoreInfoList1$Pager','%s')"%num)
        locator = (By.XPATH, "//table[@id='MoreInfoList1_DataGrid1']//tr[1]/td/a[not(contains(@href,'%s'))]" % val)
        WebDriverWait(driver, 20).until(EC.visibility_of_all_elements_located(locator))
    data = []
    page = driver.page_source
    body = etree.HTML(page)
    content_list = body.xpath('//table[@id="MoreInfoList1_DataGrid1"]/tbody/tr[@valign="top"]')
    for content in content_list:
        name = content.xpath("./td/a/text()")[0].strip()
        ggstart_time = content.xpath("./td[3]/text()")[0].strip()
        url = "http://www.xmzwggzy.com" + content.xpath("./td/a/@href")[0].strip()
        temp = [name, ggstart_time, url]
        data.append(temp)
        # print(temp)
    df = pd.DataFrame(data=data)
    df["info"] = None
    return df

def f2(driver):
    try:
        locator = (By.XPATH, '//*[@id="MoreInfoList1_Pager"]/a[contains(string(),"末页")]')
        WebDriverWait(driver, 20).until(EC.visibility_of_element_located(locator))
        driver.find_element_by_xpath('//*[@id="MoreInfoList1_Pager"]/a[contains(string(),"末页")]').click()
        total_page = driver.find_element_by_xpath("//font[@style='font-weight:Bold;color:red;']").text.strip()
    except:
        total_page = 1
    driver.quit()
    return int(total_page)


def f3(driver, url):
    driver.get(url)
    try:
        locator = (By.XPATH, "//table[@id='tblInfo']")
        WebDriverWait(driver, 10).until(EC.presence_of_all_elements_located(locator))
        flag =True
    except:
        locator = (By.XPATH, "//td[@align='left' and @valign='top']")
        WebDriverWait(driver, 10).until(EC.presence_of_all_elements_located(locator))
        flag=False
    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break
    page = driver.page_source
    soup = BeautifulSoup(page, 'lxml')
    if flag:
        div = soup.find('table', id='tblInfo')
    else:
        div=soup.findAll('td',attrs={"align":"left","valign":"top"})[0]
    return div


