def f1(driver, num):
    url = driver.current_url
    mcode = re.findall(r'/mcode=(.*)', url)[0]
    stsrt_url = url.rsplit('/', maxsplit=1)[0]
    Data = {
        'mcode': mcode,
        'clicktype': '1',
        'pageNum': '{}'.format(num),
        'keyname': '',
        'areacode': '',
    }
    user_agent = random.choice(agents)
    headers = {
        'User-Agent': user_agent,
        'Content-Type': 'application/x-www-form-urlencoded; charset=UTF-8',
        # 'Cookie': 'JSESSIONID=EAAC811EF46D1ACEEBD2055FB37DED1B; _gscu_886709310=43478808ti2a7l25; _gscbrs_886709310=1; ASP.NET_SessionId=k1kia5gqcaei112r5rmbttgl; _gscs_886709310=43478808gosq7e25|pv:5',
        'Host': '117.172.156.43:82',
    }
    sesion = requests.session()
    res = sesion.post(url=stsrt_url, headers=headers, data=Data)
    # 需要判断是否为登录后的页面
    if res.status_code == 200:
        page = res.text
        if page:
            json_data = json.loads(page)
            datas = json_data['data']
            content = datas['content']
            data_list = []
            for li in content:
                title = li['mctype']
                span = li['mckeys']
                link = 'http://117.172.156.43:82/pub/BZ_indexContent_' + li['id'] + '.html'
                tmp = [title, span, link]
                data_list.append(tmp)
                # print(tmp)
                # d = f3(driver, link)
                # print(d)

            df = pd.DataFrame(data_list)
            df['info'] = None
            return df


