endTime = time.strftime('%Y-%m-%d',time.localtime(time.time()))



def f1_data(driver, num):
    url = driver.current_url
    menuCode = re.findall(r'menuCode=(.*)', url)[0]
    stsrt_url = url.rsplit('/', maxsplit=1)[0]
    Data = {
        'page': '{}'.format(num - 1),
        'time': 'customize',
        'areaCode': '',
        'menuCode': menuCode,
        'menuTypeCode': '',
        'contentTypeCode': '',
        'keyname': '',
        'startTime': '2016-11-28',
        'endTime': endTime,
        'areaCodeFlag': '',
        # '_csrf': 'e84db454-ee48-42f5-b33a-7c61fd718952',
    }
    user_agent = random.choice(agents)
    headers = {
        'User-Agent': user_agent,
        'Content-Type': 'application/x-www-form-urlencoded; charset=UTF-8',
        'Host': 'www.dyggzy.com',
    }
    sesion = requests.session()
    res = sesion.post(url=stsrt_url, headers=headers, data=Data)
    # 需要判断是否为登录后的页面
    if res.status_code == 200:
        page = res.text
        if page:
            soup = BeautifulSoup(page, 'html.parser')
            div = soup.find('div', class_="search-result")
            lis = div.find_all('li')
            data_list = []
            for li in lis:
                a = li.find('a')
                try:
                    title = a['title'].strip()
                except:
                    title = a.text.strip()

                link = 'http://www.dyggzy.com' + a['href'].strip()
                span = li.find('span', class_="time").text.strip()

                try:
                    td = li.find_all('span')[0].text.strip()
                    tds = re.findall(r'\[(.*)\]', td)
                    td = tds[0] if tds else td
                except:
                    td = None
                a = {"diqu": td}
                a = json.dumps(a, ensure_ascii=False)
                info = a
                tmp = [title, span, link, info]
                data_list.append(tmp)

            df = pd.DataFrame(data_list)
            return df




def f1(driver, num):
    url = driver.current_url
    if ("http://www.dyggzy.com/pub/showJyxxContent/menuCode=JYGCJS" in url) or ('http://www.dyggzy.com/pub/showJyxxContent/menuCode=JYQT' in url):
        df = f1_data(driver, num)
        return df
    category_id = re.findall(r'category_id=(.*)', url)[0]
    stsrt_url = url.rsplit('/', maxsplit=1)[0]
    Data = {
        'category_id': category_id,
        'keyWord': '',
        'currentPage': '{}'.format(num),
    }
    user_agent = random.choice(agents)
    headers = {
        'User-Agent': user_agent,
        'Content-Type': 'application/x-www-form-urlencoded; charset=UTF-8',
        # 'Cookie': 'JSESSIONID=9ED1746456A738D73B1639894DDC845F.tomcat32',
        'Host': 'v4.dyggzy.com',
    }
    sesion = requests.session()
    res = sesion.post(url=stsrt_url, headers=headers, data=Data)
    # 需要判断是否为登录后的页面
    if res.status_code == 200:
        page = res.text
        if page:
            soup = BeautifulSoup(page, 'html.parser')
            div = soup.find('div', class_="tab-ul")
            lis = div.find_all('li')
            data_list = []
            for li in lis:
                a = li.find('a')
                try:
                    title = a['title'].strip()
                except:
                    title = a.text.strip()

                link = 'http://v4.dyggzy.com/' + a['href'].strip()
                span = li.find('span').text.strip()
                tmp = [title,span,link]
                data_list.append(tmp)

            df = pd.DataFrame(data_list)
            df['info'] = None
            return df


def f2(driver):
    url = driver.current_url
    if ("http://www.dyggzy.com/pub/showJyxxContent/menuCode=JYGCJS" in url) or ('http://www.dyggzy.com/pub/showJyxxContent/menuCode=JYQT' in url):
        menuCode = re.findall(r'menuCode=(.*)', url)[0]
        stsrt_url = url.rsplit('/', maxsplit=1)[0]
        Data = {
            'page': '0',
            'time': 'customize',
            'areaCode': '',
            'menuCode': menuCode,
            'menuTypeCode': '',
            'contentTypeCode': '',
            'keyname': '',
            'startTime': '2016-11-28',
            'endTime': endTime,
            'areaCodeFlag': '',
            # '_csrf': 'e84db454-ee48-42f5-b33a-7c61fd718952',
        }
        num = get_pagenum(stsrt_url, Data)
        driver.quit()
        return num

    else:
        category_id = re.findall(r'category_id=(.*)', url)[0]
        stsrt_url = url.rsplit('/', maxsplit=1)[0]
        Data = {
            'category_id': category_id,
            'keyWord': '',
            'currentPage': 1,
        }
        num = get_pageall(stsrt_url, Data)
        driver.quit()
        return num


def get_pageall(stsrt_url, Data):
    user_agent = random.choice(agents)
    headers = {
        'User-Agent': user_agent,
        'Content-Type': 'application/x-www-form-urlencoded; charset=UTF-8',
        # 'Cookie': 'JSESSIONID=9ED1746456A738D73B1639894DDC845F.tomcat32',
        'Host': 'v4.dyggzy.com',
        }

    sesion = requests.session()
    res = sesion.post(url=stsrt_url, headers=headers, data=Data)
    # 需要判断是否为登录后的页面
    if res.status_code == 200:
        page = res.text
        if page:
            soup = BeautifulSoup(page, 'html.parser')
            div = soup.find('div', class_="pagenations")
            atrs = div.find_all('a')[-2].text.strip()
            num = re.findall(r'共(\d+)页', atrs)[0]
            return int(num)



def get_pagenum(stsrt_url, Data):
    user_agent = random.choice(agents)
    headers = {
        'User-Agent': user_agent,
        'Content-Type': 'application/x-www-form-urlencoded; charset=UTF-8',
        'Host': 'www.dyggzy.com',
        }

    sesion = requests.session()
    res = sesion.post(url=stsrt_url, headers=headers, data=Data)
    # 需要判断是否为登录后的页面
    if res.status_code == 200:
        page = res.text
        # print(page)
        if page:
            soup = BeautifulSoup(page, 'html.parser')
            div = soup.find('div', class_="pagenations")
            atrs = div.find_all('span')[0].text.strip()
            num = re.findall(r'共(\d+)页', atrs)[0]
            return int(num)





def f3(driver, url):
    driver.get(url)

    locator = (By.CLASS_NAME, "ques-mid")
    WebDriverWait(driver, 10).until(EC.presence_of_all_elements_located(locator))

    # driver.execute_script("window.scrollTo(0, document.body.scrollHeight)")

    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break

    page = driver.page_source

    soup = BeautifulSoup(page, 'html.parser')

    div = soup.find('div', class_="detail-box")
    # div=div.find_all('div',class_='ewb-article')[0]

    return div


