def f1(driver, num):
    url = driver.current_url
    if 'http://www.lsggzy.com.cn/qtjyInfo/' in url:
        val = None
        locator = (By.XPATH, "//div[@class='box-text-list box-text-list-lh40 ul-odd-even']/ul/li[1]/a/span/span")
        val_1 = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text
    else:
        locator = (By.XPATH, "//div[@class='box-text-list ul-odd-even']/ul/li[1]/a/span/span")
        val = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text
    try:
        locator = (By.XPATH, "//a[@class='page-to-pi active']")
        str = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text.strip()
        cnum = int(str)
    except:
        cnum = 1

    if num != int(cnum):
        if "pageIndex" not in url:
            s = "?pageIndex=%d" % (num) if num > 1 else "?pageIndex=1"
            url = url + s
        elif num == 1:
            url = re.sub("pageIndex=[0-9]*", "pageIndex=1", url)
        else:
            s = "pageIndex=%d" % (num) if num > 1 else "pageIndex=1"
            url = re.sub("pageIndex=[0-9]*", s, url)
            # print(cnum)
        driver.get(url)
        if val != None:
            locator = (By.XPATH,"//div[@class='box-text-list ul-odd-even']/ul/li[1]/a/span/span[not(contains(string(), '%s'))]" % val)
            WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))
        else:
            locator = (By.XPATH,"//div[@class='box-text-list box-text-list-lh40 ul-odd-even']/ul/li[1]/a/span/span[not(contains(string(), '%s'))]" % val_1)
            WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))

    page = driver.page_source

    soup = BeautifulSoup(page, 'html.parser')

    table = soup.find("div", class_="box-text-list ul-odd-even")
    if table == None:
        table = soup.find("div", class_="box-text-list box-text-list-lh40 ul-odd-even")

    trs = table.find_all("li")
    data = []
    for tr in trs:
        a = tr.find('a')

        try:
            title = a['title'].strip()
        except:
            title = a.text.strip()

        link = "http://www.lsggzy.com.cn" + a['href'].strip()

        td = tr.find("span", class_="btl-time fr").text.strip()

        tmp = [title, td, link]
        data.append(tmp)

    df = pd.DataFrame(data)
    df['info'] = None
    return df





def f2(driver):
    locator = (By.XPATH, "//div[@class='box-text-list box-text-list-lh40 ul-odd-even' | @class='box-text-list ul-odd-even']/ul/li[1]/a/span/span")
    WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))
    try:
        locator = (By.XPATH, '//a[@class="page-to-last"]')
        str = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).get_attribute('href')
        num = re.findall(r'pageIndex=(\d+)', str)[0]
    except:
        num = 1

    driver.quit()
    return int(num)



def f3(driver, url):
    driver.get(url)

    locator = (By.CLASS_NAME, "layout")
    WebDriverWait(driver, 10).until(EC.presence_of_all_elements_located(locator))

    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break

    page = driver.page_source

    soup = BeautifulSoup(page, 'html.parser')

    div = soup.find('div', class_="box-container")
    # div=div.find_all('div',class_='ewb-article')[0]

    return div


