def f1(driver, num):
    locator = (By.XPATH, '//*[@id="ctl00_ContentPlaceHolder1_myGV_ctl02_HLinkGcmc"]')
    val = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text
    try:
        locator = (By.XPATH, '//tr[@class="myGVPagerCss"]/td/span[1]')
        str = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text.strip()
        cnum = int(str)
    except:
        cnum = 1

    if num != int(cnum):
        if num > int(cnum):
            t = num - int(cnum)
            for i in range(t):
                locator = (By.XPATH, '//*[@id="ctl00_ContentPlaceHolder1_myGV_ctl02_HLinkGcmc"]')
                WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))
                driver.find_element_by_link_text('下一页').click()

            locator = (By.XPATH, '//tr[@class="myGVPagerCss"]/td/span[1]')
            cnum_1 = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text.strip()
            if num != int(cnum_1):
                raise TimeoutError

        if num < int(cnum):
            t = int(cnum) - num
            for i in range(t):
                locator = (By.XPATH, '//*[@id="ctl00_ContentPlaceHolder1_myGV_ctl02_HLinkGcmc"]')
                WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))
                driver.find_element_by_link_text('上一页').click()

            locator = (By.XPATH, '//tr[@class="myGVPagerCss"]/td/span[1]')
            cnum_1 = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text.strip()
            if num != int(cnum_1):
                raise TimeoutError

    page = driver.page_source

    soup = BeautifulSoup(page, 'html.parser')

    table = soup.find("table", id='ctl00_ContentPlaceHolder1_myGV')

    trs = table.find_all("tr")
    data = []
    url = driver.current_url
    for tr in trs[1:-1]:
        a = tr.find('a')

        try:
            title = a['title'].strip()
        except:
            title = a.text.strip()
        try:
            td = tr.find("td", class_="fFbDate")
            span = td.find('span').text.strip()
        except:
            span = '-'

        if "http://cg.luzhou.gov.cn/ceinwz/WebInfo_List.aspx?" in url:
            if 'http://cg.luzhou.gov.cn/ceinwz/WebInfo_List.aspx?newsid=4205' in url:
                try:
                    ti = title.split('】', maxsplit=1)
                    tds = ti[0].split('【', maxsplit=1)[1].strip()
                    s = tds if tds else None
                    title = ti[1].strip()
                    dq = {"diqu": s}
                    info = json.dumps(dq, ensure_ascii=False)
                except:
                    info = None
                    title = title
            else:
                try:
                    ti = title.split(']', maxsplit=1)
                    tds = ti[0].split('[', maxsplit=1)[1].strip()
                    s = tds if tds else None
                    title = ti[1].strip()
                    if '※' in title:
                        title = re.sub(r'※', '', title).strip()
                    dq = {"diqu": s}
                    info = json.dumps(dq, ensure_ascii=False)
                except:
                    info = None
                    title = title

            link = "http://cg.luzhou.gov.cn/ceinwz/" + a['href'].strip()
        else:
            info = None
            link = "http://ggzy.luzhou.gov.cn/ceinwz/" + a['href'].strip()
            try:
                title = re.sub(r'\[(.*)\]', '', title).strip()
                if '※' in title:
                    title = re.sub(r'※', '', title).strip()
            except:
                title = title
                if '※' in title:
                    title = re.sub(r'※', '', title).strip()

        tmp = [title, span, link, info]
        data.append(tmp)
        # print(tmp)
    df = pd.DataFrame(data)
    return df





def f2(driver):
    locator = (By.XPATH, '//*[@id="ctl00_ContentPlaceHolder1_myGV_ctl02_HLinkGcmc"]')
    WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))
    try:
        locator = (By.XPATH, '//*[@id="ctl00_ContentPlaceHolder1_myGV_ctl23_LabelPageCount"]')
        str = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text.strip()
        num = int(str)
    except:
        num = 1

    driver.quit()
    return int(num)



def f3(driver, url):
    driver.get(url)
    url = driver.current_url
    if '/ceinwz/admin_show.aspx' in url:
        # driver.get(url)
        locator = (By.CLASS_NAME, "newsImage")
        WebDriverWait(driver, 20).until(EC.presence_of_all_elements_located(locator))

        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i = 0
        while before != after:
            before = len(driver.page_source)
            time.sleep(0.1)
            after = len(driver.page_source)
            i += 1
            if i > 5: break

        page = driver.page_source

        soup = BeautifulSoup(page, 'html.parser')

        div = soup.find('span', id="ctl00_ContentPlaceHolder1_BodyLabel")
        return div

    else:
        # driver.get(url)
        locator = (By.CLASS_NAME, "yygl")
        WebDriverWait(driver, 10).until(EC.presence_of_all_elements_located(locator))

        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i = 0
        while before != after:
            before = len(driver.page_source)
            time.sleep(0.1)
            after = len(driver.page_source)
            i += 1
            if i > 5: break

        page = driver.page_source

        soup = BeautifulSoup(page, 'html.parser')

        div = soup.find('table', width="75%")
        # div=div.find_all('div',class_='ewb-article')[0]

        return div


