def f1(driver, num):
    locator = (By.XPATH, "(//ul[@class='list-ul']/li[1]/a)[last()]")
    val = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text
    try:
        locator = (By.XPATH, "//td[@class='huifont']")
        str = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text.strip()
        cnum = re.findall(r'(\d+)/', str)[0]
    except:
        cnum = 1
    url = driver.current_url
    if num != int(cnum):
        if "moreinfo.aspx?Paging" not in url:
            s = "moreinfo.aspx?Paging=%d" % (num) if num > 1 else "moreinfo.aspx?Paging=1"
            url = url + s
        elif num == 1:
            url = re.sub("Paging=[0-9]*", "Paging=1", url)
        else:
            s = "Paging=%d" % (num) if num > 1 else "Paging=1"
            url = re.sub("Paging=[0-9]*", s, url)
        driver.get(url)

        try:
            locator = (By.XPATH, "(//ul[@class='list-ul']/li[1]/a)[last()][not(contains(string(), '%s'))]" % val)
            WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))
        except:
            driver.refresh()
            locator = (By.XPATH, "(//ul[@class='list-ul']/li[1]/a)[last()][not(contains(string(), '%s'))]" % val)
            WebDriverWait(driver, 3).until(EC.presence_of_element_located(locator))

    page = driver.page_source

    soup = BeautifulSoup(page, 'html.parser')

    table = soup.find("ul", class_="list-ul")

    trs = table.find_all("li", class_="list-li")
    data = []
    for tr in trs:
        try:
            a = tr.find_all("a")[-1]
        except:
            a = tr.find("a", class_="list-a")
        try:
            title = a['title'].strip()
        except:
            title = a.text.strip()

        link = "http://www.scncggzy.com.cn" + a['href'].strip()

        td = tr.find("span", class_='time').text.strip()

        tmp = [title, td, link]
        data.append(tmp)

    df = pd.DataFrame(data)
    df['info'] = None
    return df





def f2(driver):
    locator = (By.XPATH, "(//ul[@class='list-ul']/li[1]/a)[last()]")
    WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))

    try:
        locator = (By.XPATH, "//td[@class='huifont']")
        str = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text.strip()
        num = re.findall(r'/(\d+)', str)[0]
    except:
        num = 1

    driver.quit()
    return int(num)



def f3(driver, url):
    driver.get(url)
    html_page = driver.page_source
    if ("抱歉，系统发生了错误" in html_page) or ("出错啦！页面无法显示！" in html_page):
        return
    locator = (By.CLASS_NAME, "infodetail")
    WebDriverWait(driver, 10).until(EC.presence_of_all_elements_located(locator))


    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break

    page = driver.page_source

    soup = BeautifulSoup(page, 'html.parser')

    div = soup.find('table', style=" border:#d1d1d1 1px solid;")
    # div=div.find_all('div',class_='ewb-article')[0]

    return div


