def f1(driver, num):
    url = driver.current_url
    locator = (By.XPATH, '//*[@id="divMore"]/table/tbody/tr[1]/td[1]/a')
    val = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).text
    try:
        cnum = re.findall(r'pageid=(\d+)', url)[0]
    except:
        cnum = 1

    if num != int(cnum):
        if "pageid" not in url:
            s = "&pageid=%d" % (num) if num > 1 else "&pageid=1"
            url = url + s
        elif num == 1:
            url = re.sub("pageid=[0-9]*", "pageid=1", url)
        else:
            s = "pageid=%d" % (num) if num > 1 else "pageid=1"
            url = re.sub("pageid=[0-9]*", s, url)
            # print(cnum)
        driver.get(url)
        try:
            locator = (By.XPATH, "//*[@id='divMore']/table/tbody/tr[1]/td[1]/a[not(contains(string(), '%s'))]" % val)
            WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))
        except:
            driver.refresh()
            locator = (By.XPATH, "//*[@id='divMore']/table/tbody/tr[1]/td[1]/a[not(contains(string(), '%s'))]" % val)
            WebDriverWait(driver, 3).until(EC.presence_of_element_located(locator))

    page = driver.page_source

    soup = BeautifulSoup(page, 'html.parser')

    divs = soup.find("div", id='divMore')

    table = divs.find('table', width="100%")
    trs = table.find_all("tr")
    data = []
    for tr in trs:
        a = tr.find('a')

        try:
            title = a['title'].strip()
        except:
            title = a.text.strip()

        try:
            href = a['href'].strip()
            if "http" in href:
                link = href
            else:
                link = "http://egov.shifang.gov.cn/b/cgzx/" + href
        except:
            link = '-'

        try:
            td = tr.find("td", class_="nd")
            span = td.text.strip()
        except:
            span = '-'

        tmp = [title, span, link]
        data.append(tmp)
        # print(tmp)
        # d = f3(driver, link)
        # print(d)

    df = pd.DataFrame(data)
    df['info'] = None
    return df





def f2(driver):
    locator = (By.XPATH, '//*[@id="divMore"]/table/tbody/tr[1]/td[1]/a')
    WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator))
    try:
        locator = (By.XPATH, '(//div[@id="pagesbar"])[2]/a[last()]')
        str = WebDriverWait(driver, 10).until(EC.presence_of_element_located(locator)).get_attribute('href')
        num = re.findall(r'pageid=(\d+)', str)[0]
    except:
        num = 1

    driver.quit()
    return int(num)



def f3(driver, url):
    driver.get(url)
    url = driver.current_url
    # driver.get(url)
    locator = (By.CLASS_NAME, "maintable")
    WebDriverWait(driver, 20).until(EC.presence_of_all_elements_located(locator))

    before = len(driver.page_source)
    time.sleep(0.1)
    after = len(driver.page_source)
    i = 0
    while before != after:
        before = len(driver.page_source)
        time.sleep(0.1)
        after = len(driver.page_source)
        i += 1
        if i > 5: break

    page = driver.page_source
    soup = BeautifulSoup(page, 'html.parser')
    div = soup.find('div', id="divNews")
    return div


